import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [internships, setInternships] = useState([]);
  const [form, setForm] = useState({
    studentName: "",
    mentorName: "",
    company: "",
    duration: "",
    status: "Pending",
  });
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState("");

  // Fetch internships
  const fetchInternships = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/internships?search=${search}`);
      setInternships(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchInternships();
  }, [search]);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add or update internship
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/internships/${editId}`, form);
        setEditId(null);
      } else {
        await axios.post("http://localhost:5000/api/internships", form);
      }
      setForm({ studentName: "", mentorName: "", company: "", duration: "", status: "Pending" });
      fetchInternships();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete internship
  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/internships/${id}`);
    fetchInternships();
  };

  // Edit internship
  const handleEdit = (internship) => {
    setForm(internship);
    setEditId(internship._id);
  };

  return (
    <div className="container">
      <h1>ðŸŽ“ Internship Monitoring System</h1>

      {/* Search Box */}
      <input
        type="text"
        placeholder="ðŸ” Search by student or company"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search"
      />

      {/* Form */}
      <form onSubmit={handleSubmit} className="form">
        <input name="studentName" placeholder="Student Name" value={form.studentName} onChange={handleChange} required />
        <input name="mentorName" placeholder="Mentor Name" value={form.mentorName} onChange={handleChange} required />
        <input name="company" placeholder="Company" value={form.company} onChange={handleChange} required />
        <input name="duration" placeholder="Duration" value={form.duration} onChange={handleChange} />
        <select name="status" value={form.status} onChange={handleChange}>
          <option>Pending</option>
          <option>Ongoing</option>
          <option>Completed</option>
        </select>
        <button type="submit">{editId ? "Update Internship" : "Add Internship"}</button>
      </form>

      {/* Internship List */}
      <h2>ðŸ“‹ Internship Records</h2>
      {internships.length === 0 ? (
        <p>No internships found</p>
      ) : (
        <ul>
          {internships.map((i) => (
            <li key={i._id}>
              <div>
                <b>{i.studentName}</b> ({i.company})  
                <span className={`status ${i.status.toLowerCase()}`}>{i.status}</span>
              </div>
              <div>Mentor: {i.mentorName}</div>
              <div>Duration: {i.duration || "Not specified"}</div>
              <div className="time">
                Created: {new Date(i.createdAt).toLocaleString()}
              </div>
              <div className="actions">
                <button className="edit" onClick={() => handleEdit(i)}>Edit</button>
                <button className="delete" onClick={() => handleDelete(i._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
