import React, { useEffect, useState } from "react";
import axios from "axios";
import "./DashboardUI.css";

export default function AdminPage() {
  const [internships, setInternships] = useState([]);

  const fetchData = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/internships");
      setInternships(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="dashboard-container">
      <h2>ðŸ›  Admin Dashboard</h2>
      <p className="welcome">Manage all students, mentors & internships</p>

      <div className="grid">

        <div className="card">
          <h3>ðŸ“‹ All Internship Records</h3>
          {internships.length === 0 ? (
            <p>No internship records found.</p>
          ) : (
            internships.map((item) => (
              <div key={item._id} className="admin-internship-box">
                <p><strong>Student:</strong> {item.studentName}</p>
                <p><strong>Mentor:</strong> {item.mentorName}</p>
                <p><strong>Company:</strong> {item.company}</p>
                <p><strong>Status:</strong> {item.status}</p>
              </div>
            ))
          )}
        </div>

        <div className="card highlight">
          <h3>ðŸ“Š Overview Stats</h3>
          <p>Total Internships: {internships.length}</p>
          <p>Completed: {internships.filter(i => i.status === "Completed").length}</p>
          <p>Pending: {internships.filter(i => i.status === "Pending").length}</p>
        </div>

      </div>
    </div>
  );
}
