import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [internships, setInternships] = useState([]);
  const [form, setForm] = useState({
    studentName: "",
    mentorName: "",
    company: "",
    duration: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/api/internships")
      .then(res => setInternships(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:5000/api/internships", form)
      .then(res => setInternships([...internships, res.data]))
      .catch(err => console.log(err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/internships/${id}`)
      .then(() => setInternships(internships.filter(i => i._id !== id)))
      .catch(err => console.log(err));
  };

  return (
    <div className="container">
      <h1>ðŸŽ“ Internship Monitoring System</h1>

      <form className="form" onSubmit={handleSubmit}>
        <input name="studentName" placeholder="Student Name" onChange={handleChange} required />
        <input name="mentorName" placeholder="Mentor Name" onChange={handleChange} required />
        <input name="company" placeholder="Company" onChange={handleChange} required />
        <input name="duration" placeholder="Duration" onChange={handleChange} />
        <button type="submit">Add Internship</button>
      </form>

      <h2>ðŸ“‹ Internship List</h2>
      <ul>
        {internships.map(i => (
          <li key={i._id}>
            <b>{i.studentName}</b> - {i.company} ({i.status})
            <button onClick={() => handleDelete(i._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
