import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

// Components
import Navbar from "./components/Navbar";
import Dashboard from "./components/Dashboard";
import SearchBar from "./components/SearchBar";
import InternshipForm from "./components/InternshipForm";
import InternshipList from "./components/InternshipList";
import Footer from "./components/Footer";

function App() {
  const [internships, setInternships] = useState([]);
  const [form, setForm] = useState({
    studentName: "",
    mentorName: "",
    company: "",
    duration: "",
    status: "Pending"
  });
  const [search, setSearch] = useState("");
  const [editId, setEditId] = useState(null);

  const fetchData = async () => {
    const res = await axios.get(`http://localhost:5000/api/internships?search=${search}`);
    setInternships(res.data);
  };

  useEffect(() => {
    fetchData();
  }, [search]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editId) {
      await axios.put(`http://localhost:5000/api/internships/${editId}`, form);
      setEditId(null);
    } else {
      await axios.post("http://localhost:5000/api/internships", form);
    }

    setForm({ studentName: "", mentorName: "", company: "", duration: "", status: "Pending" });
    fetchData();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/internships/${id}`);
    fetchData();
  };

  const handleEdit = (record) => {
    setForm(record);
    setEditId(record._id);
  };

  return (
    <div className="container">

      <Navbar />

      <Dashboard internships={internships} />

      <SearchBar search={search} setSearch={setSearch} />

      <InternshipForm
        form={form}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        editId={editId}
      />

      <h2>ðŸ“‹ Internship Records</h2>

      <InternshipList
        internships={internships}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />

      <Footer />
    </div>
  );
}

export default App;
